        M[5] = 0x01;
        M[8] = 0x8000000000000000;

        uint r = 72;
        uint w = 8;
        uint size = M.length * 8;

        uint[25] memory S;
        uint i;
        uint y;
        uint x;
        /*Absorbing Phase*/
        for (i = 0; i < size / r; i++) {
            for (y = 0; y < 5; y++) {
                for (x = 0; x < 5; x++) {
                    if ((x + 5 * y) < (r / w)) {
                        S[5 * x + y] = S[5 * x + y] ^ M[i * 9 + x + 5 * y];
                    }
                }
            }
            S = keccak_f(S);
        }

        /*Squeezing phase*/
        uint[16] memory result;
        uint b = 0;
        while (b < 16) {
            for (y = 0; y < 5; y++) {
                for (x = 0; x < 5; x++) {
                    if ((x + 5 * y) < (r / w) && (b < 16)) {
                        result[b] = S[5 * x + y] & 0xFFFFFFFF;
                        result[b + 1] = S[5 * x + y] / 0x100000000;
                        b += 2;
                    }
                }
            }
        }

        return result;
    }

